// SPDX-License-Identifier: Unlicense
pragma solidity >=0.8.17;

// Dependencies
import {ICygnusTerminal} from "./interfaces/ICygnusTerminal.sol";
import {ERC20Permit} from "./ERC20Permit.sol";
import {ReentrancyGuard} from "./utils/ReentrancyGuard.sol";

// Libraries
import {SafeTransferLib} from "./libraries/SafeTransferLib.sol";
import {FixedPointMathLib} from "./libraries/FixedPointMathLib.sol";
import {SafeCastLib} from "./libraries/SafeCastLib.sol";

// Interfaces
import {IOrbiter} from "./interfaces/IOrbiter.sol";
import {IHangar18} from "./interfaces/IHangar18.sol";
import {ICygnusNebulaOracle} from "./interfaces/ICygnusNebulaOracle.sol";

// Permit2
import {IAllowanceTransfer} from "./interfaces/IAllowanceTransfer.sol";

/**
 *  @title  CygnusTerminal
 *  @author CygnusDAO
 *  @notice Contract used to mint Collateral and Borrow tokens. Both Collateral/Borrow arms of Cygnus mint here
 *          to get the vault token (CygUSD for stablecoin deposits and CygLP for Liquidity deposits).
 */
contract CygnusTerminal is ICygnusTerminal, ERC20Permit, ReentrancyGuard {
    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            1. LIBRARIES
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @custom:library SafeTransferLib ERC20 transfer library that gracefully handles missing return values.
     */
    using SafeTransferLib for address;

    /**
     *  @custom:library FixedPointMathLib Arithmetic library with operations for fixed-point numbers.
     */
    using FixedPointMathLib for uint256;

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            2. STORAGE
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ─────────────────────────────────────────────── Public ────────────────────────────────────────────────  */

    /**
     *  @inheritdoc ICygnusTerminal
     */
    IAllowanceTransfer public constant override PERMIT2 =
        IAllowanceTransfer(0x000000000022D473030F116dDEE9F6B43aC78BA3);

    /**
     *  @inheritdoc ICygnusTerminal
     */
    IHangar18 public immutable override hangar18;

    /**
     *  @inheritdoc ICygnusTerminal
     */
    ICygnusNebulaOracle public immutable override cygnusNebulaOracle;

    /**
     *  @inheritdoc ICygnusTerminal
     */
    address public immutable override underlying;

    /**
     *  @inheritdoc ICygnusTerminal
     */
    uint256 public immutable override shuttleId;

    /**
     *  @notice The contract's totalBalance is stored as a uint160 which is the max asset allowance the Permit2 router allows.
     *  @inheritdoc ICygnusTerminal
     */
    uint160 public override totalBalance;

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            3. CONSTRUCTOR
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @notice Constructs tokens for both Collateral and Borrow arms
     */
    constructor() {
        // Get immutables from deployer contract who is msg.sender of deployments
        // Factory, asset, borrow/collateral, oracle, lending pool ID
        (hangar18, underlying, , cygnusNebulaOracle, shuttleId) = IOrbiter(msg.sender).shuttleParameters();
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            4. MODIFIERS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @notice We mark as virtual in case need we need to also update before interaction (ie yield bearing tokens)
     *  @custom:modifier update Updates the total balance var in terms of its underlying
     */
    modifier update() virtual {
        _;
        // Update after deposit
        updateInternal();
    }

    /**
     *  @custom:modifier cygnusAdmin Controls important parameters in both Collateral and Borrow contracts 👽
     */
    modifier cygnusAdmin() {
        checkAdmin();
        _;
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            5. CONSTANT FUNCTIONS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── Internal ───────────────────────────────────────────────  */

    /**
     *  @notice Internal check for msg.sender admin, checks factory's current admin 👽
     */
    function checkAdmin() internal view {
        // Current admin from the factory
        address admin = hangar18.admin();

        /// @custom:error MsgSenderNotAdmin Avoid unless caller is Cygnus Admin
        if (msg.sender != admin) revert CygnusTerminal__MsgSenderNotAdmin();
    }

    /**
     *  @notice Checks the `token` balance of this contract
     *  @param token The token to view balance of
     *  @return amount This contract's `token` balance
     */
    function contractBalanceOf(address token) internal view returns (uint256) {
        // Our balance of `token`
        return token.balanceOf(address(this));
    }

    /*  ─────────────────────────────────────────────── Public ────────────────────────────────────────────────  */

    /**
     *  @inheritdoc ICygnusTerminal
     */
    function exchangeRate() public virtual override returns (uint256) {
        // Gas savings if non-zero
        uint256 _totalSupply = totalSupply;

        // Compute the exchange rate as the total balance of the underlying asset divided by the total supply of
        // the vault token. If there is no supply for this token, return the initial exchange rate of 1:1.
        return _totalSupply == 0 ? 1e18 : uint256(totalBalance).divWad(_totalSupply);
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            6. NON-CONSTANT FUNCTIONS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── Internal ───────────────────────────────────────────────  */

    /**
     *  @notice Updates this contract's total balance in terms of its underlying
     */
    function updateInternal() internal {
        // Get current balanceOf this contract
        uint256 balance = previewTotalBalance();

        /// @custom:event Sync
        emit Sync(totalBalance = SafeCastLib.toUint160(balance));
    }

    /**
     *  @notice Preview the total balance of the underlying we own from the strategy (if any)
     *  @return balance This contract's balance of the underlying asset
     */
    function previewTotalBalance() internal virtual returns (uint256 balance) {
        // Get current balanceOf this contract
        balance = contractBalanceOf(underlying);
    }

    /**
     *  @notice Internal hook for deposits into strategies
     *  @param assets The amount of assets to deposit in the strategy
     */
    function afterDepositInternal(uint256 assets) internal virtual {}

    /**
     *  @notice Internal hook for withdrawals from strategies
     *  @param assets The amount of assets to withdraw from the strategy
     */
    function beforeWithdrawInternal(uint256 assets) internal virtual {}

    /*  ────────────────────────────────────────────── External ───────────────────────────────────────────────  */

    /**
     *  @inheritdoc ICygnusTerminal
     *  @custom:security non-reentrant
     */
    function deposit(
        uint256 assets,
        address recipient,
        IAllowanceTransfer.PermitSingle calldata _permit,
        bytes calldata signature
    ) external override nonReentrant update returns (uint256 shares) {
        // Check for deposit fee
        uint256 balanceBefore = previewTotalBalance();

        // Check for permit (users can just approve permit2 and skip this by passing an empty
        // `_permit` and an empty `signature`)
        if (signature.length > 0) {
            // Set allowance using permit
            PERMIT2.permit(
                // The owner of the tokens being approved.
                // We only allow the owner of the tokens to be the depositor, but
                // recipient can be set to another address
                msg.sender,
                // Data signed over by the owner specifying the terms of approval
                _permit,
                // The owner's signature over the permit data that was the result
                // of signing the EIP712 hash of `_permit`
                signature
            );
        }

        // Transfer underlying to vault
        PERMIT2.transferFrom(msg.sender, address(this), SafeCastLib.toUint160(assets), underlying);

        // Deposit in strategy
        afterDepositInternal(assets);

        // Balance after
        uint256 balanceAfter = previewTotalBalance();

        // Get the shares amount
        shares = (balanceAfter - balanceBefore).divWad(exchangeRate());

        /// @custom:error CantMintZeroShares Avoid minting no shares
        if (shares == 0) revert CygnusTerminal__CantMintZeroShares();

        // Avoid first depositor front-running & update shares - only for the first pool depositor
        if (totalSupply == 0) {
            // Update shares for first depositor
            shares -= 1000;

            // Lock initial tokens
            _mint(address(0xdEaD), 1000);
        }

        // Mint shares and emit Transfer event
        _mint(recipient, shares);

        /// @custom:event Deposit
        emit Deposit(msg.sender, recipient, assets, shares);
    }

    /**
     *  @inheritdoc ICygnusTerminal
     *  @custom:security non-reentrant
     */
    function redeem(
        uint256 shares,
        address recipient,
        address owner
    ) external override nonReentrant update returns (uint256 assets) {
        // Withdraw flow
        if (msg.sender != owner) {
            // Check msg.sender's allowance
            uint256 allowed = _allowances[owner][msg.sender]; // Saves gas for limited approvals.

            // Reverts on underflow
            if (allowed != type(uint256).max) _allowances[owner][msg.sender] = allowed - shares;
        }

        // Check current exchange rate
        assets = shares.mulWad(exchangeRate());

        /// @custom:error CantRedeemZeroAssets Avoid redeeming no assets
        if (assets <= 0) revert CygnusTerminal__CantRedeemZeroAssets();

        // Withdraw assets from the strategy (if any)
        beforeWithdrawInternal(assets);

        // Burn shares
        _burn(owner, shares);

        // Transfer assets to recipient
        underlying.safeTransfer(recipient, assets);

        /// @custom:event Withdraw
        emit Withdraw(msg.sender, recipient, owner, assets, shares);
    }

    /**
     *  @inheritdoc ICygnusTerminal
     *  @custom:security non-reentrant only-admin 👽
     */
    function sweepToken(address token) external override nonReentrant cygnusAdmin {
        /// @custom:error CantSweepUnderlying Avoid sweeping underlying
        if (token == underlying) revert CygnusTerminal__CantSweepUnderlying();

        // Balance this contract has of the erc20 token we are recovering
        uint256 balance = contractBalanceOf(token);

        // Transfer token
        token.safeTransfer(msg.sender, balance);
    }
}

contracts/cygnus-core/ERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

import {IERC20} from "./interfaces/IERC20.sol";

// NOTE: Modified from the original author, removed constructor and immutable metadata:
//       https://github.com/PaulRBerg/prb-contracts/blob/main/src/token/erc20/ERC20.sol

/// @title ERC20
/// @author Paul Razvan Berg
contract ERC20 is IERC20 {
    /*//////////////////////////////////////////////////////////////////////////
                                USER-FACING STORAGE
    //////////////////////////////////////////////////////////////////////////*/

    /// @inheritdoc IERC20
    string public override name;

    /// @inheritdoc IERC20
    string public override symbol;

    /// @inheritdoc IERC20
    uint8 public override decimals;

    /// @inheritdoc IERC20
    uint256 public override totalSupply;

    /*//////////////////////////////////////////////////////////////////////////
                                  INTERNAL STORAGE
    //////////////////////////////////////////////////////////////////////////*/

    /// @dev Internal mapping of allowances.
    mapping(address => mapping(address => uint256)) internal _allowances;

    /// @dev Internal mapping of balances.
    mapping(address => uint256) internal _balances;

    /*//////////////////////////////////////////////////////////////////////////
                           USER-FACING CONSTANT FUNCTIONS
    //////////////////////////////////////////////////////////////////////////*/

    /// @inheritdoc IERC20
    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function balanceOf(address account) public view virtual override returns (uint256) {
        return _balances[account];
    }

    /*//////////////////////////////////////////////////////////////////////////
                         USER-FACING NON-CONSTANT FUNCTIONS
    //////////////////////////////////////////////////////////////////////////*/

    /// @inheritdoc IERC20
    function approve(address spender, uint256 value) public virtual override returns (bool) {
        _approve(msg.sender, spender, value);
        return true;
    }

    /// @inheritdoc IERC20
    function decreaseAllowance(address spender, uint256 value) public virtual override returns (bool) {
        // Calculate the new allowance.
        uint256 newAllowance = _allowances[msg.sender][spender] - value;

        // Make the approval.
        _approve(msg.sender, spender, newAllowance);
        return true;
    }

    /// @inheritdoc IERC20
    function increaseAllowance(address spender, uint256 value) public virtual override returns (bool) {
        // Calculate the new allowance.
        uint256 newAllowance = _allowances[msg.sender][spender] + value;

        // Make the approval.
        _approve(msg.sender, spender, newAllowance);
        return true;
    }

    /// @inheritdoc IERC20
    function transfer(address to, uint256 amount) public virtual override returns (bool) {
        _transfer(msg.sender, to, amount);
        return true;
    }

    /// @inheritdoc IERC20
    function transferFrom(address from, address to, uint256 amount) public virtual override returns (bool) {
        // Checks: the spender's allowance is sufficient.
        address spender = msg.sender;
        uint256 currentAllowance = _allowances[from][spender];
        if (currentAllowance < amount) {
            revert ERC20_InsufficientAllowance(from, spender, currentAllowance, amount);
        }

        // Effects: update the allowance.
        unchecked {
            _approve(from, spender, currentAllowance - amount);
        }

        // Checks, Effects and Interactions: make the transfer.
        _transfer(from, to, amount);

        return true;
    }

    /*//////////////////////////////////////////////////////////////////////////
                          INTERNAL NON-CONSTANT FUNCTIONS
    //////////////////////////////////////////////////////////////////////////*/

    /// @notice Sets `value` as the allowance of `spender` over the `owner`s tokens.
    ///
    /// @dev Emits an {Approval} event.
    ///
    /// Requirements:
    ///
    /// - `owner` must not be the zero address.
    /// - `spender` must not be the zero address.
    function _approve(address owner, address spender, uint256 value) internal virtual {
        // Checks: `owner` is not the zero address.
        if (owner == address(0)) {
            revert ERC20_ApproveOwnerZeroAddress();
        }

        // Checks: `spender` is not the zero address.
        if (spender == address(0)) {
            revert ERC20_ApproveSpenderZeroAddress();
        }

        // Effects: update the allowance.
        _allowances[owner][spender] = value;

        // Emit an event.
        emit Approval(owner, spender, value);
    }

    /// @notice Destroys `amount` tokens from `holder`, decreasing the token supply.
    ///
    /// @dev Emits a {Transfer} event.
    ///
    /// Requirements:
    ///
    /// - `holder` must have at least `amount` tokens.
    function _burn(address holder, uint256 amount) internal virtual {
        // Checks: `holder` is not the zero address.
        if (holder == address(0)) {
            revert ERC20_BurnHolderZeroAddress();
        }

        // Effects: burn the tokens.
        _balances[holder] -= amount;

        // Effects: reduce the total supply.
        unchecked {
            // Underflow not possible: amount <= account balance <= total supply.
            totalSupply -= amount;
        }

        // Emit an event.
        emit Transfer(holder, address(0), amount);
    }

    /// @notice Prints new `amount` tokens into existence and assigns them to `beneficiary`, increasing the
    /// total supply.
    ///
    /// @dev Emits a {Transfer} event.
    ///
    /// Requirements:
    ///
    /// - The beneficiary's balance and the total supply must not overflow.
    function _mint(address beneficiary, uint256 amount) internal {
        // Checks: `beneficiary` is not the zero address.
        if (beneficiary == address(0)) {
            revert ERC20_MintBeneficiaryZeroAddress();
        }

        /// Effects: increase the total supply.
        totalSupply += amount;

        /// Effects: mint the new tokens.
        unchecked {
            // Overflow not possible: `balance + amount` is at most `totalSupply + amount`, which is checked above.
            _balances[beneficiary] += amount;
        }

        // Emit an event.
        emit Transfer(address(0), beneficiary, amount);
    }

    /// @notice Moves `amount` tokens from `from` to `to`.
    ///
    /// @dev Emits a {Transfer} event.
    ///
    /// Requirements:
    ///
    /// - `from` must not be the zero address.
    /// - `to` must not be the zero address.
    /// - `from` must have a balance of at least `amount`.
    function _transfer(address from, address to, uint256 amount) internal virtual {
        // Checks: `from` is not the zero address.
        if (from == address(0)) {
            revert ERC20_TransferFromZeroAddress();
        }

        // Checks: `to` is not the zero address.
        if (to == address(0)) {
            revert ERC20_TransferToZeroAddress();
        }

        // Checks: `from` has enough balance.
        uint256 fromBalance = _balances[from];
        if (fromBalance < amount) {
            revert ERC20_FromInsufficientBalance(fromBalance, amount);
        }

        // Effects: update the balance of `from` and `to`..
        unchecked {
            _balances[from] = fromBalance - amount;
            // Overflow not possible: the sum of all balances is capped by the total supply, and the sum is preserved by
            // decrementing then incrementing.
            _balances[to] += amount;
        }

        // Emit an event.
        emit Transfer(from, to, amount);
    }
}

contracts/cygnus-core/ERC20Permit.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

import {ERC20} from "./ERC20.sol";
import {IERC20Permit} from "./interfaces/IERC20Permit.sol";

/// @title ERC20Permit
/// @author Paul Razvan Berg
contract ERC20Permit is
    IERC20Permit, // 1 inherited component
    ERC20 // 1 inherited component
{
    /// PUBLIC STORAGE ///

    /// @inheritdoc IERC20Permit
    bytes32 public immutable override DOMAIN_SEPARATOR;

    /// @inheritdoc IERC20Permit
    bytes32 public constant override PERMIT_TYPEHASH =
        keccak256("Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)");

    /// @inheritdoc IERC20Permit
    mapping(address => uint256) public override nonces;

    /// @inheritdoc IERC20Permit
    string public constant override version = "1";

    /// CONSTRUCTOR ///

    constructor() {
        uint256 chainId = block.chainid;
        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"),
                keccak256(bytes(name)),
                keccak256(bytes(version)),
                chainId,
                address(this)
            )
        );
    }

    /// PUBLIC NON-CONSTANT FUNCTIONS ///

    /// @inheritdoc IERC20Permit
    function permit(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) public override {
        // Checks: `owner` is not the zero address.
        if (owner == address(0)) {
            revert ERC20Permit_OwnerZeroAddress();
        }

        // Checks: `spender` is not the zero address.
        if (spender == address(0)) {
            revert ERC20Permit_SpenderZeroAddress();
        }

        // Checks: the deadline is in the future (or at least at present).
        if (deadline < block.timestamp) {
            revert ERC20Permit_PermitExpired(block.timestamp, deadline);
        }

        // It's safe to use unchecked here because the nonce cannot realistically overflow, ever.
        bytes32 hashStruct;
        unchecked {
            hashStruct = keccak256(abi.encode(PERMIT_TYPEHASH, owner, spender, value, nonces[owner]++, deadline));
        }

        // EIP-712 messages always start with "\x19\x01".
        bytes32 digest = keccak256(abi.encodePacked("\x19\x01", DOMAIN_SEPARATOR, hashStruct));
        address recoveredOwner = ecrecover(digest, v, r, s);

        // Checks: `recoveredOwner` is not the zero address.
        if (recoveredOwner == address(0)) {
            revert ERC20Permit_RecoveredOwnerZeroAddress();
        }

        // Checks: `recoveredOwner` is not the same as `owner`.
        if (recoveredOwner != owner) {
            revert ERC20Permit_InvalidSignature(owner, v, r, s);
        }

        // Effects: update the allowance.
        _allowances[owner][spender] = value;

        // Emit an event.
        emit Approval(owner, spender, value);
    }
}

contracts/cygnus-core/interfaces/Aggrega.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

interface AggregatorV3Interface {
    function decimals() external view returns (uint8);

    function description() external view returns (string memory);

    function version() external view returns (uint256);

    // getRoundData and latestRoundData should both raise "No data present"
    // if they do not have data to report, instead of returning unset values
    // which could be misinterpreted as actual reported values.
    function getRoundData(
        uint80 _roundId
    )
        external
        view
        returns (uint80 roundId, int256 answer, uint256 startedAt, uint256 updatedAt, uint80 answeredInRound);

    function latestRoundData()
        external
        view
        returns (uint80 roundId, int256 answer, uint256 startedAt, uint256 updatedAt, uint80 answeredInRound);
}

